def count_taken_around(seat_layout, x, y):
    neighbours = []
    for r in range(y-1 if y > 0 else y, y+2 if y < len(seat_layout)-1 else y+1):
        for c in range(x-1 if x > 0 else x, x+2 if x < len(seat_layout[0])-1 else x+1):
            neighbours.append(seat_layout[r][c])

    count =  neighbours.count('#')
    if seat_layout[y][x] == "#":
        count -= 1

    return count

def generate_final_layout(seat_layout):
    current_layout =  seat_layout

    while True:
        change = 0
        new_layout = []
        for y in range(len(current_layout)):
            new_row = []
            for x in range(len(current_layout[0])):
                if current_layout[y][x] == ".":
                    new_row.append(".")
                elif current_layout[y][x] == "L" and count_taken_around(current_layout, x, y) == 0:
                    new_row.append("#")
                    change += 1
                elif current_layout[y][x] == "#" and count_taken_around(current_layout, x, y) > 3:
                    new_row.append("L")
                    change += 1
                elif current_layout[y][x] == "L" and count_taken_around(current_layout, x, y) > 0:
                    new_row.append("L")
                else:
                    new_row.append("#")
            new_layout.append(new_row)

        current_layout = new_layout

        if change == 0:
            return current_layout


def count_all_taken_seats(seat_layout):
    return sum(row.count('#') for row in seat_layout)

final_layout = generate_final_layout(seat_layout)
taken_seats = count_all_taken_seats(final_layout)

print(taken_seats)

