def find_up(seat_layout, x, y):
    while (y - 1) >= 0:
        y -= 1
        if seat_layout[y][x] == "#":
            return True
        elif seat_layout[y][x] == "L":
            return False
    return False

def find_down(seat_layout, x, y):
    while (y + 1) < len(seat_layout):
        y += 1
        if seat_layout[y][x] == "#":
            return True
        elif seat_layout[y][x] == "L":
            return False
    return False

def find_left(seat_layout, x, y):
    while (x - 1) >= 0:
        x -= 1
        if seat_layout[y][x] == "#":
            return True
        elif seat_layout[y][x] == "L":
            return False
    return False

def find_right(seat_layout, x, y):
    while (x + 1) < len(seat_layout[0]):
        x += 1
        if seat_layout[y][x] == "#":
            return True
        elif seat_layout[y][x] == "L":
            return False
    return False

def find_left_up_diag(seat_layout, x, y):
    while (x - 1) >= 0 and (y - 1) >= 0:
        x -= 1
        y -= 1
        if seat_layout[y][x] == "#":
            return True
        elif seat_layout[y][x] == "L":
            return False
    return False

def find_left_down_diag(seat_layout, x, y):
    while (x - 1) >= 0 and (y + 1) < len(seat_layout):
        x -= 1
        y += 1
        if seat_layout[y][x] == "#":
            return True
        elif seat_layout[y][x] == "L":
            return False
    return False
    
def find_right_up_diag(seat_layout, x, y):
    while (x + 1) < len(seat_layout[0]) and (y - 1) >= 0:
        x += 1
        y -= 1
        if seat_layout[y][x] == "#":
            return True
        elif seat_layout[y][x] == "L":
            return False
    return False

def find_right_down_diag(seat_layout, x, y):
    while (x + 1) < len(seat_layout[0]) and (y + 1) < len(seat_layout):
        x += 1
        y += 1
        if seat_layout[y][x] == "#":
            return True
        elif seat_layout[y][x] == "L":
            return False
    return False

def count_taken_in_lines(seat_layout, x, y):
    test_list = [find_up, find_down, find_left, find_right,
                 find_left_up_diag, find_left_down_diag,
                 find_right_up_diag, find_right_down_diag]

    count = [funct(seat_layout, x, y) for funct in test_list].count(True)

    return count


def generate_final_layout(seat_layout):
    current_layout =  seat_layout

    while True:
        change = 0
        new_layout = []
        for y in range(len(current_layout)):
            new_row = []
            for x in range(len(current_layout[0])):
                if current_layout[y][x] == ".":
                    new_row.append(".")
                elif current_layout[y][x] == "L" and count_taken_in_lines(current_layout, x, y) == 0:
                    new_row.append("#")
                    change += 1
                elif current_layout[y][x] == "#" and count_taken_in_lines(current_layout, x, y) > 4:
                    new_row.append("L")
                    change += 1
                elif current_layout[y][x] == "L" and count_taken_in_lines(current_layout, x, y) > 0:
                    new_row.append("L")
                else:
                    new_row.append("#")
            new_layout.append(new_row)

        current_layout = new_layout

        if change == 0:
            return current_layout


def count_all_taken_seats(seat_layout):
    return sum(row.count('#') for row in seat_layout)

final_layout = generate_final_layout(seat_layout)
taken_seats = count_all_taken_seats(final_layout)

print(taken_seats)